$(document).ready(function(){var e=$("#tweets"),t=e.data("twitter-username");$.getJSON("/tweets/"+t+"/",function(t){for(num=t.length-1;num>=0;num--){tweet=t[num];num==0?e.prepend('<div class="col-xs-12 col-sm-6 col-md-4 col-lg-3 hidden-md style-'+(num%3+1)+'"><div class="tweet"><p><small>'+tweet.text_urlized_atlinked+'</small></p><p class="created_at"><a href="http://twitter.com/#!/'+tweet.user.screen_name+"/status/"+tweet.id_str+'">'+tweet.created_timesince+"</a></p></div></div>"):e.prepend('<div class="col-xs-12 col-sm-6 col-md-4 col-lg-3 style-'+(num%3+1)+'"><div class="tweet"><p><small>'+tweet.text_urlized_atlinked+'</small></p><p class="created_at"><a href="http://twitter.com/#!/'+tweet.user.screen_name+"/status/"+tweet.id_str+'">'+tweet.created_timesince+"</a></p></div></div>")}})});